mapper.select:
SELECT tran_no, tran_status_code	
FROM transaction 
WHERE prod_no = #{value}

mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, reciver_phone = #{reciverPhonge}, demailaddr = #{dlvyAddr}, dlvy_request = #{dlvyRequest}, dlvy_date = #{dlvyDate}
WHERE tran_no = #{tranNo}

mapper.select:
		SELECT *
    	FROM (SELECT inner_table.*, ROWNUM AS row_seq
        	FROM (SELECT t.tran_no, t.prod_no, t.buyer_id, t.payment_option, t.receiver_name, t.receiver_phone,
        	t.demailaddr, t.dlvy_request, t.tran_status_code, t.order_data, t.dlvy_date, t.count, p.prod_name, p.price
        	FROM transaction t, product p
			WHERE t.buyer_id = #{buyer.userId}	 
 			) inner_table
        	WHERE ROWNUM < = #{endRowNum} )
    	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
