mapper.select:
SELECT tran_no, tran_status_code	
FROM transaction 
WHERE prod_no = #{value}

mapper.update:
UPDATE transaction
payment_option = #{paymentOption}, receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, demailaddr = #{dlvyAddr}, dlvy_request = #{dlvyRequest}, dlvy_date = #{dlvyDate}
WHERE tran_no = #{tranNo}
